","keccak256":"0xc7702b775ffda42d217c4671b846d93f691f8271cae9537dbd2b15930dbe582e","license":"UNLICENSED"},"contracts/interfaces/ICarbonOffsetBatches.sol":{"content":"// SPDX-FileCopyrightText: 2021 Toucan Labs
//
// SPDX-License-Identifier: UNLICENSED

// If you encounter a vulnerability or an issue, please contact <security@toucan.earth> or visit security.toucan.earth
pragma solidity 0.8.14;

import {BatchStatus} from '../CarbonOffsetBatchesTypes.sol';

interface ICarbonOffsetBatches {
    function getConfirmationStatus(uint256 tokenId)
        external
        view
        returns (BatchStatus);

    function getBatchNFTData(uint256 tokenId)
        external
        view
        returns (
            uint256,
            uint256,
            BatchStatus
        );

    function setStatusForDetokenizationOrRetirement(
        uint256 tokenId,
        BatchStatus newStatus
    ) external;

    function split(
        uint256 tokenId,
